#!/usr/bin/env node

const express = require("express");
const path = require("path");
const app = express();
const port = 3000;

// Serve the static files generated by Docusaurus
app.use(express.static(path.join(__dirname, "build")));

// Handle custom server-side redirects from redirects.json
const redirects = require("./redirects.json");
app.use((req, res, next) => {
  if (req.path in redirects) {
    const to = redirects[req.path];
    // send temporary redirect
    res.redirect(302, to);
  } else {
    next();
  }
});

app.use((req, res) => {
  res.status(404).sendFile(path.join(__dirname, "build", "404.html"));
});

// Serve the Docusaurus site on the specified port
app.listen(port, () => {
  console.log(`Server running at http://localhost:${port}/`);
});
