typescriptSdkUsageCode:
import { Pipedrive } from 'pipedrive-typescript-sdk';

const pipedrive = new Pipedrive({
    // Base 64 encoded string containing the `client_id` and `client_secret` values. The header value should be `Basic <base64(client_id:client_secret)>`.
    username: "USERNAME",
    password: "PASSWORD",
    apiKey: "API_TOKEN",
    // For more information, see https://pipedrive.readme.io/docs/marketplace-oauth-authorization
    clientId: "CLIENT_ID",
    clientSecret: "CLIENT_SECRET",
    redirectUri: "REDIRECT_URI"
})

typescriptSdkFirstRequestCode:
// Requesting authorization
const requestAuthorizationResponse = pipedrive.oauth.requestAuthorization({
    clientId: "CLIENT_ID"
    redirectUri: "REDIRECT_URI"
})
