typescriptSdkUsageCode:
import { SoundCloud } from 'soundcloud-typescript-sdk';

const soundCloud = new SoundCloud({
    // The string "OAuth" followed by the user’s token: OAuth ACCESS_TOKEN
    authHeader: "AUTHORIZATION",
})

typescriptSdkFirstRequestCode:
// The OAuth2 authorization endpoint. Your app redirects a user to this endpoint, allowing them to delegate access to their account.
const authorizeUserResponse = soundCloud.oauth.authorizeUser({
    clientId: "some client"
    redirectUri: "https://soundcloud.com"
    responseType: "code"
    state: "encrypted_session_info"
})
